namespace Mageplaza\AutoRelated\Model\Config\Source;

use Magento\Framework\Option\ArrayInterface;

/**
 * Class DisplayMode
 * @package Mageplaza\AutoRelated\Model\Config\Source
 */
class DisplayMode implements ArrayInterface
{
    const TYPE_AJAX  = '0';
    const TYPE_BLOCK = '1';

    /**
     * @return array
     */
    public function toOptionArray()
    {
        $options = [];
        foreach ($this->toArray() as $value => $label) {
            $options[] = [
                'value' => $value,
                'label' => $label
            ];
        }

        return $options;
    }

    /**
     * Get options in "key-value" format
     *
     * @return array
     */
    public function toArray()
    {
        return [self::TYPE_AJAX => __('Ajax Display'), self::TYPE_BLOCK => __('Block Display')];
    }
}
